float getAngleFromPacket(rplidar_response_capsule_measurement_nodes_t* node)
{
	float angle = (float)(node->start_angle_sync_q6 & 0x7FFF);
	uint16_t angle64 = node->start_angle_sync_q6 & 0x7FFF;
	angle = angle/64;
	return angle;
}
